mapper.select:
SELECT meeting_num, introduce, meeting_profile, meeting_name, meeting_adress, meeting_hobby FROM (SELECT /*+INDEX_DESC(teamb_meeting xpkteamb_meeting) */ rownum rn, meeting_num, introduce, meeting_profile, meeting_name, meeting_adress, meeting_hobby FROM teamb_meeting A WHERE
rownum <= #{cri.pageNum} * #{cri.amount}
AND #{userid} IN (SELECT userid FROM teamb_meeting_member B WHERE A.MEETING_NUM = B.meeting_num)) C WHERE rn > (#{cri.pageNum} -1) * #{cri.amount} AND #{userid} IN (SELECT userid FROM teamb_meeting_member B WHERE C.MEETING_NUM = B.meeting_num)

mapper.insert:
insert into teamb_meeting_member (join_code,
						  		  userid,
						  		  join_date,
						  		  meeting_num
						  )
values (#{meeting_num} || '_' || #{userid},
		#{userid},
		SYSDATE,
		#{meeting_num}
		)
